sources.style.scss:
body {
  min-height: 100vh;
  font-family: "Helvetica";
  font-size: 14px;
  color: #444444;
  background-color: #f8f8f8;

  .loader {
    height: 100vh;
  }

  .content,
  .content-container {
    display: none;
    height: 100vh;
    width: 500px;
  }

  .preview,
  .preview-container {
    display: none;
    height: 100vh;
  }

  .heading-wrapper {
    width: 500px;

    .heading-text {
      width: 100%;
      text-align: left;
    }
  }

  .preview-heading-wrapper {
    width: 740px;

    .heading-text {
      width: 100%;
      text-align: left;
    }
  }

  .config-wrapper {
    width: 500px;
    padding: 20px;
    height: 250px;
    background: white;
    border: 1px solid #e9e9e9;
    border-radius: 6px;

    .config-row {
      height: 218px;
    }

    .control-wrapper {
      width: 120px;

      .label {
        padding: 5px 3px;
        color: #888888;
      }
    }

    .seat-wrapper {
      width: 60px;
    }

    .price-wrapper {
      vertical-align: top;
      width: 75px;

      #cost-value {
        padding-top: 8px;
        padding-bottom: 4px;
        font-size: 16px;
        font-weight: 500;
      }

      .label-price {
        padding: 5px 0;
        color: #888888;
      }
    }
  }

  .preview-container {
    .preview-box {
      width: 350px;
      height: 350px;
      background: white;
      border: 1px solid #e9e9e9;
      padding: 20px;
      margin: 20px;
      border-radius: 6px;

      .preview-title {
        font-weight: bold;
        margin: 15px;
      }

      .preview-table {
        width: 100%;
        height: 210px;
        margin: 30px;

        td {
          font-size: 20px;
        }

        .preview-table-title {
          color: #bbb;
          width: 108px;
        }

        .prev-values {
          color: #888;
          font-weight: bold;
        }

        .updated-values {
          color: #30aabc;
          font-weight: bold;
        }
      }
    }
  }

  .button-wrapper {
    width: 500px;
    text-align: right;
  }

  .preview-button-wrapper {
    width: 740px;
    text-align: left;
  }
}

// Utilities
.hidden {
  display: none !important;
}

.fas {
  font-size: 20px;
  position: relative;
  top: 1px;
  margin-right: 5px;
}
